const topics = [
    'sensor/plant1/boiler',
    'sensor/plant1/turbine',
    'sensor/plant2/compressor',
    'sensor/plant2/generator',
    'sensor/plant3/hvac',
    'sensor/plant3/motor',
    'sensor/warehouse/cold_storage',
    'sensor/warehouse/conveyor'
];

const randomFloat = (min, max) => +(Math.random() * (max - min) + min).toFixed(2);

// Pick a random topic
const topic = topics[Math.floor(Math.random() * topics.length)];

// Generate sensor values (occasionally out of range)
const breach = Math.random() < 0.2;

msg.topic = topic;
msg.payload = {
    temperature: breach && Math.random() < 0.3 ? randomFloat(60, 85) : randomFloat(-10, 60),
    humidity: breach && Math.random() < 0.3 ? randomFloat(90, 100) : randomFloat(10, 90),
    voltage: breach && Math.random() < 0.3 ? randomFloat(260, 320) : randomFloat(180, 260),
    current: breach && Math.random() < 0.3 ? randomFloat(30, 50) : randomFloat(0, 30),
    pressure: breach && Math.random() < 0.3 ? randomFloat(1050, 1200) : randomFloat(950, 1050)
};

return msg;
